import React from 'react'
import Tab from 'react-bootstrap/Tab'
import Nav from 'react-bootstrap/Nav'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

export default class StudentCourses extends React.Component {
  constructor() {
    super()

    this.state = {
      student: this.props.student,
      courses: []
    }
  }

  componentDidMount = () => {
    const requestedCourses = this.props.student.courses.map(
      (courseId) => {
        requestCourse(courseId)
      }
    )
    this.setState({
      courses: requestedCourses
    })
  }

  requestCourse = (courseId) => {
    return axios.get(`http://localhost:5000/courses/getCourse/${courseId}`)
    .then(response => {
      return response.data
    })
    .catch(err => console.log(err))
  }

  render () {
    return(
      <React.Fragment>

      </React.Fragment>
    )
  }
}
