import React from 'react'
import Tab from 'react-bootstrap/Tab'
import Nav from 'react-bootstrap/Nav'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

export default class StudentCourses extends React.Component {
  constructor() {
    super()

    this.state = {
      student: this.props.student,
      courses: []
    }
  }

  componentDidMount = () => {
    const requestedCourses = this.props.student.courses.map(
      (courseId) => {
        requestCourse(courseId)
      }
    )
    this.setState({
      courses: requestedCourses
    })
  }

  requestCourse = (courseId) => {
    return axios.get(`http://localhost:5000/courses/getCourse/${courseId}`)
    .then(response => {
      return response.data
    })
    .catch(err => console.log(err))
  }

  render () {
    const {courseNavItems, courseTabItems, courseItems} = courses.map(
      (course) => (
        <Nav.Item>
          <Nav.Link eventKey={course.name}>{course.name}</Nav.Link>
        </Nav.Item>
      )
    )
    return(
      <React.Fragment>
        <h5>Course List</h5>
        <Tab.Container id="left-tabs-example" defaultActiveKey="first">
          <Row>
            <Col sm={3}>
              <Nav variant="pills" className="flex-column">
                {courseNavItems}
              </Nav>
            </Col>
            <Col sm={9}>
              <Tab.Content>
                <Tab.Pane eventKey="first">
                  <Sonnet />
                </Tab.Pane>
                <Tab.Pane eventKey="second">
                  <Sonnet />
                </Tab.Pane>
              </Tab.Content>
            </Col>
          </Row>
        </Tab.Container>
      </React.Fragment>
    )
  }
}
