import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from "react-router-dom";
import Container from 'react-bootstrap/Container'

import StudentLogin from '../student/student-login'
import StudentHome from './'

export const StudentAuthContext = React.createContext();

const initialState = {
  isAuthenticated: false,
  email: null
};

const reducer = (state, action) => {
  switch (action.type) {
    case "LOGIN":
      return {
        ...state,
        isAuthenticated: true,
        email: action.payload.user
      };
    case "LOGOUT":
      return {
        ...state,
        isAuthenticated: false,
        email: null
      };
    default:
      return state;
  }
};

export default class StudentApp extends Component {
  constructor(props) {
    super(props);
  }

  render () {
    const [state, dispatch] = React.useReducer(reducer, initialState);
    return (
      <Router>
        <Container>
          <StudentAuthContext.Provider
            value={{
              state,
              dispatch
            }}
          >
            {!state.isAuthenticated ? <StudentLogin /> : <Home />}
          </StudentAuthContext.Provider>
        </Container>
      </Router>
    );
  }
}
