import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from "react-router-dom";
import Container from 'react-bootstrap/Container'

import StudentNavbar from "./student-navbar.component";
import StudentLogin from "./student-login.component";
import CreateStudentAccount from "./student-create-account.component";

export const StudentAuthContext = React.createContext();
const initialState = {
  isAuthenticated: false,
  user: null,
  token: null,
};
const reducer = (state, action) => {
  switch (action.type) {
    case "LOGIN":
      localStorage.setItem("user", JSON.stringify(action.payload.user));
      localStorage.setItem("token", JSON.stringify(action.payload.token));
      return {
        ...state,
        isAuthenticated: true,
        user: action.payload.user,
        token: action.payload.token
      };
    case "LOGOUT":
      localStorage.clear();
      return {
        ...state,
        isAuthenticated: false,
        user: null
      };
    default:
      return state;
  }
};

export default class Student extends Component {
  render () {
    return (
      <Router>
        <Container>
          <StudentNavbar />
          <br />
          <Route path="/student/login" component = {StudentLogin} />
          <Route path="/student/create" component = {CreateStudentAccount} />
        </Container>
        <StudentAuthContext.Provider>
          <div className="App"></div>
        </StudentAuthContext.Provider>
      </Router>
    );
  }
}
