import React from 'react';
import Nav from 'react-bootstrap/Nav'
import Col from 'react-bootstrap/Col'
import Tab from 'react-bootstrap/Tab'
import Row from 'react-bootstrap'

import CourseFlag from './instructorFlag'

export default class InstructorFlags extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      instructor: props.instructor,
      course: [],
      
    }
  }

  render () {
    const courseNavItems = this.state.courses.map(
      (course) => (
        <Nav.Item>
          <Nav.Link eventKey={course.name}>{course.name}</Nav.Link>
        </Nav.Item>
      )
    )
    const courseTabItems = this.state.courses.map(
      (course) => (
          <Tab.Pane eventKey={course.name}>
            <CourseFlag course={course} key={course.id}/>
          </Tab.Pane>
        )
    )
    return (
      <Tab.Container id="left-tabs-example" defaultActiveKey="first">
      <Row>
        <Col sm={3}>
          <Nav variant="pills" className="flex-column">
            {courseNavItems}
          </Nav>
        </Col>
        <Col sm={9}>
          <Tab.Content>
            {courseTabItems}
          </Tab.Content>
        </Col>
      </Row>
    </Tab.Container>
    )
  }
}
