    this.setState({
      email: e.target.value
    });
  }

  onChangePassword(e) {
    this.setState({
      password: e.target.value
    });
  }

  logOut() {
    this.setState({
      email: '',
      password: '',
      failure: false,
      isAuthenticated: false
    })
  }

