import React, {Component} from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert'
import axios from 'axios';

export default class StudentProfile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      firstName: props.student.firstName,
      lastName: props.student.lastName,
      email: props.student.email,
      success: false
    }
  }

  onFirstNameChange = (e) => {
    this.setState({
      firstName: e
    })
  }

  onLastNameChange = (e) => {
    this.setState({
      lastName: e
    })
  }

  componentDidUpdate = (prevProps) => {
    if (prevProps.student.email !== this.props.student.email) {
    this.setState({
      firstName: this.props.student.firstName,
      lastName: this.props.student.lastName,
      email: this.props.student.email,
      success: false
    })
    }
  }

  editProfile = () => {
    axios.put(`http://localhost:5000/students/email/${this.props.email}`)
      .then(res => {
        this.setState({
          success: true
        })
      })
      .catch(err => {
        console.log(err)
      })
  }

  render () {
    const flag = this.state.success ?
      (<Alert variant="success">
          <p>
            Profile updated successfully
          </p>
          <hr />
          <div className="d-flex justify-content-end">
            <Button onClick={() => this.setState({success: false})} variant="outline-success">
              Close
            </Button>
          </div>
        </Alert>)
      : null
    return (
      <Form>
        {flag}
        <h1>Profile</h1>
        <Form.Group as={Row} controlId="formPlaintextFirstName" onChange={this.onFirstNameChange}>
          <Form.Label column sm="2">
            First Name
          </Form.Label>
          <Col sm="10">
            <Form.Control plaintext defaultValue={this.state.firstName} />
          </Col>
        </Form.Group>
        <Form.Group as={Row} controlId="formPlaintextLastName" onChange={this.onLastNameChange}>
          <Form.Label column sm="2">
            Last Name
          </Form.Label>
          <Col sm="10">
            <Form.Control plaintext defaultValue={this.state.lastName} />
          </Col>
        </Form.Group>
        <Form.Group as={Row} controlId="formPlaintextEmail">
          <Form.Label column sm="2">
            Email
          </Form.Label>
          <Col sm="10">
            <Form.Control plaintext readOnly defaultValue={this.state.email} />
          </Col>
        </Form.Group>
        <Button variant="primary" type="submit" onSubmit={this.editProfile}>
          Edit
        </Button>
      </Form>
    );
  }
}
