import React from 'react'
import Button from 'react-bootstrap/Button'
import axios from 'axios'

import Flag from './flag.component'

export default class CourseFlagList extends React.Component {

  onClick = () => {
    axios.put(`http://localhost:5000/courses/refreshFlags`)
      .then()
      .catch(err => console.log(err))
  }
  
  render () {
    const flagComponents = this.props.course.universalFlags.map(
      (flag) => (
        <Flag flag={flag} />
      )
    )
    return (
      <React.Fragment>
        <Button variant="outline-dark" type="submit" onSubmit={this.onClick}>Refresh Flags</Button>
        <br/>
        <br/>
        { flagComponents }
      </React.Fragment>
    )
  }
}
