import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from "react-router-dom";
import Container from 'react-bootstrap/Container'

import StudentLogin from '../student/student-login.component'
import StudentHome from './studentapp-home.component'

export const StudentAuthContext = React.createContext();

const initialState = {
  isAuthenticated: false,
  email: null
};

const reducer = (state, action) => {
  switch (action.type) {
    case "LOGIN":
      return {
        ...state,
        isAuthenticated: true,
        email: action.email
      };
    case "LOGOUT":
      return {
        ...state,
        isAuthenticated: false,
        email: null
      };
    default:
      return state;
  }
};

function StudentApp() {
    const [state, dispatch] = React.useReducer(reducer, initialState);
    return (
      <Router>
        <Container>
          <StudentAuthContext.Provider
            value={{
              state,
              dispatch
            }}
          >
            {!state.isAuthenticated ? <StudentLogin /> : <StudentHome />}
          </StudentAuthContext.Provider>
        </Container>
      </Router>
    );
}

export default StudentApp;
