import React from 'react'
import axios from 'axios'
import Tab from 'react-bootstrap/Tab'
import Nav from 'react-bootstrap/Nav'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

import StudentCourse from './student-course.component'

export default class StudentCourses extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      student: props.student,
      courses: []
    }
  }

  componentDidMount = () => {
    const requestedCourses = this.props.student.courses.map(
      (courseId) => {
        this.requestCourse(courseId)
      }
    )
    this.setState({
      courses: requestedCourses
    })
  }

  requestCourse = (courseId) => {
    return axios.get(`http://localhost:5000/courses/getCourse/${courseId}`)
    .then(response => {
      return response.data
    })
    .catch(err => console.log(err))
  }

  render () {
    console.log(this.state.courses)
    const courseNavItems = this.state.courses.map(
      (course) => (
        <Nav.Item>
          <Nav.Link eventKey={course.name}>{course.name}</Nav.Link>
        </Nav.Item>
      )
    )
    const courseTabItems = this.state.courses.map(
      (course) => (
          <Tab.Pane eventKey={course.name}>
            <StudentCourse course={course} />
          </Tab.Pane>
        )
    )
    return(
      <React.Fragment>
        <h5>Course List</h5>
        <Tab.Container id="left-tabs-example" defaultActiveKey="first">
          <Row>
            <Col sm={3}>
              <Nav variant="pills" className="flex-column">
                {courseNavItems}
              </Nav>
            </Col>
            <Col sm={9}>
              <Tab.Content>
                {courseTabItems}
              </Tab.Content>
            </Col>
          </Row>
        </Tab.Container>
      </React.Fragment>
    )
  }
}
